// contracts/IBBU.sol
// SPDX-License-Identifier: Apache-2.0
pragma solidity >=0.8.2;

interface IBBU{

  struct Game{
      bool initialized;
      uint256 bet;
      address[2] players;
      bytes32[2] playersMoveIntention;
      uint256[2] playersMove;
      bytes32[2] playersMoveSeed;
      uint8 gameStatus;
      uint8 winner;
      bool nativeCurrency;
  }

  struct Player{
    bool initialized;
    string pseudo;
    uint256 gamesPlayed;
    uint256 gamesWon;
    uint256 gamesLost;
    uint256 gamesDraw;
    uint256 level;
    uint8[3] cardsLevel;
    uint256 pendingNativeRewards;
    uint256 pendingTokenRewards;
  }

  function register(string memory pseudo) external;
  function unregister() external;
  function createGame(string memory gameId, uint256 bet) external payable;
  function joinGame(string memory gameId) external payable returns(bool);
  function submitIntention(string memory  gameId, uint8 playerId, bytes32 moveIntention) external returns(bool);
  function submitMove(string memory  gameId, uint8 playerId, uint256 move, uint256 seed) external returns(uint8);
  function claimNativeRewards(uint256 amount) external;
  function claimTokenRewards(uint256 amount) external;

  event PlayerRegistered(address indexed player, string  pseudo);
  event GameCreated(address indexed player1, string gameId, uint256 bet, bool nativeCurrency);
  event GameStarted(string gameId, address indexed player1, address indexed player2);
  event GameFinished(string gameId, uint8 winner);
}
